#include<iostream>

 using namespace std;

 int row = 3, col = 4;
 int matrix[3][4] = {{1,2,3,4},{11,21,31,41},
{12,22,32,42}};

 void transpose()
 {
 int temp;

 for(int i=0;i<row;i++)
 {
 for(int j=i;j<col;j++)
 {
 temp = matrix[i][j];
 matrix[i][j] = matrix[j][i];
 matrix[j][i] = temp;
 }
 }

 temp = col;
 col = row;
 row = temp;
 }

 void display()
 {
 for(int i=0;i<row;i++)
 {
 for(int j=0;j<col;j++)
 {
 coutmatrix[i][j]"	";
 }

 cout<<endl;
 }
 }

 int main()
 {
 cout<<"MATRIX : "<<endl;
 display();

 transpose();

 cout"<<"TRANSPOSE MATRIX : "<<endl;
 display();

 return 0;
 }
